mapper.select:
select

from output_value_detail
where output_value_id = #{outputValueId, jdbcType=BIGINT}

mapper.insert:
insert into output_value_detail (relation_id, sub_contractor_id, output_value_id, 
  output_value_rate, sub_contractor_amount, migrant_worker_amount, 
  create_user_id, create_time, last_modify_time
  )
values (#{relationId,jdbcType=BIGINT}, #{subContractorId,jdbcType=INTEGER}, #{outputValueId,jdbcType=BIGINT}, 
  #{outputValueRate,jdbcType=DECIMAL}, #{subContractorAmount,jdbcType=DECIMAL}, #{migrantWorkerAmount,jdbcType=DECIMAL}, 
  #{createUserId,jdbcType=INTEGER}, #{createTime,jdbcType=TIMESTAMP}, #{lastModifyTime,jdbcType=TIMESTAMP}
  )
